 private void metodocalcular(Double total){
       String Diagnostico="";
       //en este metodo segun el total de su indice corporal al usuario se le astignará su rango
       if (total<16){
       Diagnostico="Criterio de ingreso en hospital";
       }else     
       if (total<16 && total>17){
       Diagnostico="infrapeso";
       }else    
       if (total<17 && total>16){
       Diagnostico="bajo peso";
       }else
       if (total<18 && total>17){
       Diagnostico= "peso normal (saludable)";
       }else
       if (total<25 && total>18){
       Diagnostico= "sobrepeso (obesidad de grado I)";
       }else
       if (total<30 && total>25){
       Diagnostico= "sobrepeso crónico (obesidad de grado II)";
       }else
       if (total<35 && total>30){
       Diagnostico= "obesidad premórbida (obesidad de grado III)";
       }else
       if (total<40 && total>35){
       Diagnostico= "obesidad premórbida (obesidad de grado III)";
       }else
       if (total>40){
       Diagnostico="obesidad mórbida (obesidad de grado IV)";
       }
          // pasamos a string nuestro total para desplegar en pantalla
          String totalf= total.toString();
          jTextField3.setText(totalf);
          jTextField4.setText(Diagnostico);
           
     }
 
